(427, 'core', 'admin_spammer', 0, '$member, $email', '\n{$email->language->addToStack(\"email_flagged_as_spammer\", FALSE)}\n<br />\n<br />\n<table width=\"100%\" cellpadding=\"15\" cellspacing=\"0\" border=\"0\" bgcolor=\"#f9f9f9\" style=\'color: #5b5b5b; font-size: 13px\'>\n	<tr>\n		<td dir=\'{dir}\' width=\"80\" valign=\"top\" class=\'hidePhone\' style=\'width: 0; max-height: 0; overflow: hidden; float: left;\'>\n			<img src=\'{$member->photo}\' width=\'80\' height=\'80\' style=\'border: 1px solid #d7d7d7; vertical-align: middle;\'>\n		</td>\n		<td dir=\'{dir}\'>\n			<table width=\"100%\" cellpadding=\"0\" cellspacing=\"0\" border=\"0\" class=\'responsive_table\'>\n				<tr class=\'responsive_row\'>\n					<td dir=\'{dir}\' width=\"115\" style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\"><strong>{$email->language->addToStack(\"username\", FALSE)}:</strong>&nbsp;&nbsp;</td>\n					<td dir=\'{dir}\' style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\">{$member->name}</td>\n				</tr>\n				<tr class=\'responsive_row\'>\n					<td dir=\'{dir}\' width=\"115\" style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\"><strong>{$email->language->addToStack(\"email_address\", FALSE)}:</strong>&nbsp;&nbsp;</td>\n					<td dir=\'{dir}\' style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\">{$member->email}</td>\n				</tr>\n				<tr class=\'responsive_row\'>\n					<td dir=\'{dir}\' width=\"115\" style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\"><strong>{$email->language->addToStack(\"members_ip_address\", FALSE)}:</strong>&nbsp;&nbsp;</td>\n					<td dir=\'{dir}\' style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\">{$member->ip_address}</td>\n				</tr>\n				<tr class=\'responsive_row\'>\n					<td dir=\'{dir}\' width=\"115\" style=\"font-family: \'Helvetica Neue\', helvetica, sans-serif;\"><strong>{$email->language->addToStack(\"members_joined\", FALSE)}:</strong>&nbsp;&nbsp;</td>\n					<td dir=\'{dir}\' st